import React, { Component } from 'react';
import './Signin.css';
// import Input from '../UI/Input/Input';
// import '../UI/Input/Input.css';

import { useHistory } from 'react-router-dom';

class Signin extends Component {

	handleSignin = (event) => {
		history.push("/register");
		console.log(this.state);
		this.props.onRouteChange('regsiter');
	}

	render() {
		return (
			<div>
				{/* <form>

					<Input inputtype="input" type="text" name="username" placeholder="Enter your Username" label="Username :  " />
					<Input inputtype="input" type="text" name="password" placeholder="Enter your Password" label="Password :  " />
				</form> */}

				<form>
					<div class="form-group row">
						<label for="username" class="col-sm-2 col-form-label">Username</label>
						<div class="col-sm-6">
							<input type="username" class="form-control" id="username" placeholder="Enter your Username" />
						</div>
					</div>
					<div class="form-group row">
					<label for="password" class="col-sm-2 col-form-label">Password</label>
					<div class="col-sm-6">
						<input type="password" class="form-control" id="password" placeholder="Enter your Password" />
					</div>
				</div>
				</form>

				<div class="form-group row">
					<div class="col-sm-6 offset-sm-2">
						{/* <button type="submit" class="btn btn-primary">Signin</button> */}
						<input className="btn-lg btn btn-dark indent" type="submit" value="Sign In" onClick={this.handleSignin}>
						{/* <Link to=""></Link> */}
						</input>
					</div>
				</div>
			</div>
		);
	}
}

export default Signin;
