import React, { Component } from 'react';
import './Signin.css';
// import Input from '../UI/Input/Input';
// import '../UI/Input/Input.css';

import { withRouter } from 'react-router-dom'

class Signin extends Component {
	Constructor(props) {
		this.state = {
			'username': '',
			'password': ''
		}

	}
	handleUsername = e => {
		console.log(e.target.value);
		this.setState({
			'username': e.target.value
		})
	}
	handlePassword = e => {
		console.log(e.target.value);
		this.setState({
			'password': e.target.value
		})
	}
	render() {

		const Button = withRouter(({ history }) => (
			<button
				className="btn-lg btn btn-dark indent"
				type='submit'
				value="Sign In"
				onClick={
					() => {
						history.push('/dashboard')
					}
				}
			>
				Sign In
			</button>
		))

		return (

			<div>
				{/* <form>

					<Input inputtype="input" type="text" name="username" placeholder="Enter your Username" label="Username :  " />
					<Input inputtype="input" type="text" name="password" placeholder="Enter your Password" label="Password :  " />
				</form> */}

				<form>
					<div class="form-group row">
						<label for="username" class="col-sm-2 col-form-label">Username</label>
						<div class="col-sm-6">
							<input type="username" class="form-control" id="username" placeholder="Enter your Username"  onChange={this.handleUsername} > </input>
						</div>
					</div>
					<div class="form-group row">
						<label for="password" class="col-sm-2 col-form-label">Password</label>
						<div class="col-sm-6">
							<input type="password" class="form-control" id="password" placeholder="Enter your Password"  onChange={this.handlePassword} />
						</div>
					</div>
				</form>

				<div class="form-group row">
					<div class="col-sm-6 offset-sm-2">
						<Button />
					</div>
				</div>
			</div>
		);
	}
}

export default Signin;
