import React, { Component } from 'react';
import './Welcome.css';

import HomePage from '../../Components/HomePage/HomePage';
import Signin from '../../Components/Signin/Signin';
import Register from '../../Components/Register/Register';


class App extends Component {

	state = {
		isLoggedin: false,
		ifSignupPage: false,
	}

	onRegisterhandler = () => {
		this.setState({
			ifSignupPage: true,
		})
	}

	handleSignin = (event) => {
		event.preventDefault();
		console.log(this.state)
		this.props.onRouteChange('home')
	}

	render = () => {
		return (
			<div>
				<div className="App">
					{
						this.state.isLoggedin ?
							null :
							<div>
								<HomePage />
								<div className="App">
									{
										this.state.ifSignupPage ?
											<Register /> :
											<div>
												<Signin />
												<h5 class="indent">Don't have Account ?  <br></br>
													<a href="#" onClick={this.onRegisterhandler}>
														Sign Up
													</a>
												</h5>
											</div>
									}
								</div>
							</div>
					}
				</div>
			</div>
		);
	}
}


export default App;
