import React, { Component } from 'react';
import './App.css';

import Home from './Components/Welcome/Welcome';

import { BrowserRouter, Route, Switch } from 'react-router-dom';


class App extends Component {

	state = {
		isLoggedin: false,
		ifSignupPage: false,
	}

	onRegisterhandler = () => {
		this.setState({
			ifSignupPage: true,
		})
	}



	handleSignin = (event) => {
		event.preventDefault();
		console.log(this.state)
		this.props.onRouteChange('home')
	}

	render = () => {
		return (
			<main>
				<Switch>
					<div>
						<Route path="/home" render={() => <div>Home</div>} />

						<div className="App">
							{
								this.state.isLoggedin ?
									null :
									<div>
										<HomePage />
										<div className="App">
											{
												this.state.ifSignupPage ?
													<Register /> :
													<div>
														<Signin />
														<h5 class="indent">Don't have Account?  <br></br>
															<a href="#" onClick={this.onRegisterhandler}>
																Sign Up
													</a>
														</h5>
													</div>
											}
										</div>
									</div>
							}
						</div>
					</div>
				</Switch>
			</main>



		);
	}

}


export default App;
